clock x, t;

int[0, MaxSegmentNumber - 1] seg_idx;

void enqueue() {
    int tmp;
    queue[queue_len++] = id;
    if (queue_len > 0) {
        int i = queue_len - 1;
        while (i >= 1 && Tasks[queue[i]].pri < Tasks[queue[i - 1]].pri) {
            tmp = queue[i - 1];
            queue[i - 1] = queue[i];
            queue[i] = tmp;
            i--;
        }
    }
}

int period() {
    return Tasks[id].period;
}

int deadline() {
    return Tasks[id].deadline;
}

int offset() {
    return Tasks[id].offset;
}

int pri() {
    return Tasks[id].pri;
}

int s_min() {
    return Tasks[id].segments[seg_idx].s_min;
}

int s_max() {
    return Tasks[id].segments[seg_idx].s_max;
}

int c_min() {
    return Tasks[id].segments[seg_idx].c_min;
}

int c_max() {
    return Tasks[id].segments[seg_idx].c_max;
}

bool is_last_segment() {
    return seg_idx == Tasks[id].number_of_segments - 1;
}

void dequeue() {
    int i = 0;
    queue_len -= 1;
    while (i < queue_len)
    {
        queue[i] = queue[i + 1];
        i++;
    }
    queue[i] = 0;
}

id_t front() {
    return queue[0];
}

bool job_ready() {
    return queue_len > 0;
}

bool processor_avail() {
    return avail_processors > 0;
}
